import styled from 'styled-components';
import {Link} from 'react-router-dom';

export const InfoContainer = styled.div`
    color: #000;
    background: ${({lightBg}) => (lightBg ? '#f9f9f9' : '#fcfcfc')}; 
  
    
    @media screen and (max-width: 768px) {
        padding: 100px 0;
    }
`;

export const InfoWrapper = styled.div`
    display: grid;
    z-index: 1;
    height: 860px;
    width: 100%;
    max-width: 1100px;
    margin-right: auto;
    margin-left: auto;
    padding: 0 24px;
    justify-content: center;
`;

export const InfoRow = styled.div`
    display: grid;
    grid-auto-columns: minmax(auto, 1fr);
    align-items: center;
    grid-template-areas: ${({imgStart}) => (
        imgStart ? `'col2 col1'` : `'col1 col2'` )};
   

    @media screen and (max-width: 768px) {
        grid-template-areas: ${({imgStart}) => (
            imgStart ? `'col1' 'col2'` : `'col1 col1' 'col2 col2'` )}; 
         
    } 
`;

export const Column1 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col1;
`;

export const Column2 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col2;
`;

export const TextWrapper = styled.div`
    max-width: 540px;
    padding-top: 0;
    padding-bottom: 60px;
`;

export const TopLine = styled.p`
    color: #000;
    font-size: 46px;
    line-height: 16px;
    font-weight: 700;
    text-transform: uppercase;
    margin-bottom: 200px;
    height: 100px;

@media screen and (max-width: 480px) {
    font-size: 3rem;
}
`;

export const Heading = styled.h1`
    margin-bottom: 24px;
    font-size: 28px;
    line-height: 1.1; 
    font-weight: 600;
    color: ${({ lightText }) => (lightText ? '#010606' : '#fff')
    }; 

    @media screen and (max-width: 480px) {
        font-size: 32px;
    }
`;

export const Subtitle = styled.p`
    max-width: 440px;
    margin-bottom: 35px;
    font-size: 18px;
    line-height: 24px;
    color: ${({ darkText }) => (darkText ? '#010606' : '#c0c0c0c')};
`;

export const BtnWrap = styled.h1`
    display: flex;
    justify-content: flex-start;
`;

export const Button = styled(Link)`
    right-padding: 50px;
`

export const ImgWrap = styled.div`
    max-width: 555px;
    height: 100%;
    position: sticky;
`;

export const Img = styled.img`
    width: 100%;
    margin: 0 0 10px 0;
    padding-right: 0;
`;
